settings.markdownCss:
/* ═══════════════════════════════════════════════════════════════
   ECO ENERGY - DESKTOP HOME TILES (≥960px)
   Industrial Precision Design | 5 Horizontal Cards
   Font: DM Sans | Accent: #1976D2 | Grid: Flex
   ═══════════════════════════════════════════════════════════════ */

@import url('https://fonts.googleapis.com/css2?family=DM+Sans:opsz,wght@9..40,400;9..40,500;9..40,600;9..40,700&family=JetBrains+Mono:wght@500&display=swap');

:host {
    display: block;
    height: 100%;
    --eco-primary: #1976D2;
    --eco-primary-light: #42A5F5;
    --eco-primary-dark: #0D47A1;
    --eco-surface: #FFFFFF;
    --eco-bg: #F5F7FA;
    --eco-text: #1A1F36;
    --eco-text-muted: #5E6278;
    --eco-border: rgba(0, 0, 0, 0.06);
    --eco-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.04);
    --eco-shadow-md: 0 4px 12px rgba(0, 0, 0, 0.08);
    --eco-shadow-lg: 0 8px 32px rgba(0, 0, 0, 0.12);
    --eco-radius: 8px;
    --eco-transition: 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

/* ─── Container ─── */
.tile-container {
    display: flex;
    flex-wrap: nowrap;
    gap: 1.25rem;
    width: 100%;
    height: 100%;
    justify-content: center;
    align-items: center;
    padding: 2rem;
    box-sizing: border-box;
    background: var(--eco-bg);
}

/* ─── Card Base ─── */
.eco-card {
    flex: 1;
    max-width: 240px;
    min-width: 180px;
    height: 280px;
    background: var(--eco-surface);
    border-radius: var(--eco-radius);
    box-shadow: var(--eco-shadow-md);
    border: 1px solid var(--eco-border);
    display: flex;
    flex-direction: column;
    position: relative;
    overflow: hidden;
    cursor: pointer;
    transition:
        transform var(--eco-transition),
        box-shadow var(--eco-transition);
}

.eco-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--eco-shadow-lg);
}

.eco-card:active {
    transform: translateY(-4px);
    transition-duration: 0.1s;
}

/* ─── Accent Line ─── */
.card-accent {
    height: 4px;
    background: linear-gradient(90deg,
        var(--eco-primary) 0%,
        var(--eco-primary-light) 100%);
    flex-shrink: 0;
    position: relative;
    overflow: hidden;
}

.card-accent::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg,
        transparent 0%,
        rgba(255, 255, 255, 0.4) 50%,
        transparent 100%);
    transition: left 0.6s ease;
}

.eco-card:hover .card-accent::after {
    left: 100%;
}

/* ─── Content ─── */
.card-content {
    flex: 1;
    padding: 1.5rem 1.25rem 1rem;
    display: flex;
    flex-direction: column;
    position: relative;
}

/* ─── Number Badge ─── */
.card-number {
    position: absolute;
    top: 1rem;
    right: 1rem;
    font-family: 'JetBrains Mono', monospace;
    font-size: 0.625rem;
    font-weight: 500;
    letter-spacing: 0.05em;
    color: var(--eco-primary);
    background: rgba(25, 118, 210, 0.08);
    padding: 0.25rem 0.5rem;
    border-radius: 4px;
    transition: transform var(--eco-transition);
}

.eco-card:hover .card-number {
    transform: translateY(-2px);
}

/* ─── Icon ─── */
.card-icon-wrapper {
    width: 48px;
    height: 48px;
    border-radius: 10px;
    background: linear-gradient(135deg,
        rgba(25, 118, 210, 0.1) 0%,
        rgba(25, 118, 210, 0.04) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1rem;
    transition: all var(--eco-transition);
}

.card-icon-wrapper mat-icon {
    font-size: 24px;
    width: 24px;
    height: 24px;
    color: var(--eco-primary);
    transition: transform var(--eco-transition);
}

.eco-card:hover .card-icon-wrapper {
    background: linear-gradient(135deg,
        rgba(25, 118, 210, 0.15) 0%,
        rgba(25, 118, 210, 0.08) 100%);
}

.eco-card:hover .card-icon-wrapper mat-icon {
    transform: scale(1.1);
}

/* ─── Typography ─── */
.card-title {
    font-family: 'DM Sans', sans-serif;
    font-size: 1rem;
    font-weight: 600;
    color: var(--eco-text);
    margin: 0 0 0.5rem;
    line-height: 1.3;
    letter-spacing: -0.01em;
}

.card-description {
    font-family: 'DM Sans', sans-serif;
    font-size: 0.8125rem;
    line-height: 1.55;
    color: var(--eco-text-muted);
    margin: 0;
    flex: 1;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

/* ─── Footer ─── */
.card-footer {
    padding: 0.875rem 1.25rem;
    border-top: 1px solid var(--eco-border);
    background: rgba(0, 0, 0, 0.01);
}

.card-link {
    font-family: 'DM Sans', sans-serif;
    display: inline-flex;
    align-items: center;
    gap: 0.375rem;
    font-size: 0.8125rem;
    font-weight: 500;
    color: var(--eco-primary);
    text-decoration: none;
    transition: gap var(--eco-transition);
}

.card-link .arrow-icon {
    font-size: 16px;
    width: 16px;
    height: 16px;
    transition: transform var(--eco-transition);
}

.eco-card:hover .card-link {
    gap: 0.625rem;
}

.eco-card:hover .card-link .arrow-icon {
    transform: translateX(2px);
}

