settings.cardCss:
/* ═══════════════════════════════════════════════════════════════
   ECO ENERGY - CLEAN/MINIMAL STEPPER DESIGN
   Prefix: belimo-ra
   Font: Roboto | Border-Radius: 12px | Transitions: 0.25s ease
   ═══════════════════════════════════════════════════════════════ */

.belimo-ra {
    width: 100%;
    height: 100%;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
    padding: 1.75rem;
    background: #ffffff;
    overflow: auto;
    font-family: "Roboto", "Helvetica Neue", sans-serif;
    color: #1a1a2e;
}

/* ─── Header ─── */
.belimo-ra__header {
    display: flex;
    flex-direction: column;
    gap: 0.375rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid rgba(0, 0, 0, 0.06);
}

.belimo-ra__title {
    font-size: 1.25rem;
    font-weight: 600;
    line-height: 1.3;
    letter-spacing: -0.01em;
    color: #1a1a2e;
}

.belimo-ra__subtitle {
    font-size: 0.875rem;
    color: #6b7280;
    line-height: 1.5;
}

/* ─── Progress Stepper ─── */
.belimo-ra__progress {
    display: flex;
    align-items: center;
    gap: 0;
    user-select: none;
    padding: 0.5rem 0;
}

.belimo-ra__dot {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 2px solid rgba(0, 0, 0, 0.12);
    background: #ffffff;
    font-size: 0.75rem;
    font-weight: 600;
    color: rgba(0, 0, 0, 0.4);
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    z-index: 1;
    transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
    font-family: "Roboto", "Helvetica Neue", sans-serif;
}

.belimo-ra__dot:hover {
    border-color: rgba(0, 0, 0, 0.24);
    background: rgba(0, 0, 0, 0.02);
}

.belimo-ra__dot.is-active {
    border-color: var(--tb-primary-500, #2196f3);
    background: var(--tb-primary-500, #2196f3);
    color: #ffffff;
    box-shadow: 0 2px 8px rgba(33, 150, 243, 0.3);
    transform: scale(1.05);
}

.belimo-ra__dot.is-completed {
    border-color: var(--tb-primary-500, #2196f3);
    background: rgba(33, 150, 243, 0.1);
    color: var(--tb-primary-500, #2196f3);
}

.belimo-ra__line {
    height: 2px;
    flex: 1;
    background: rgba(0, 0, 0, 0.08);
    border-radius: 1px;
    margin: 0 -1px;
    transition: background 0.25s ease;
}

.belimo-ra__line.is-completed {
    background: linear-gradient(90deg,
        var(--tb-primary-500, #2196f3) 0%,
        rgba(33, 150, 243, 0.3) 100%);
}

/* ─── Steps Content ─── */
.belimo-ra__steps {
    flex: 1;
    min-height: 0;
    overflow-y: auto;
}

.belimo-ra__step {
    display: none;
    animation: stepFadeIn 0.3s ease-out;
}

@keyframes stepFadeIn {
    from {
        opacity: 0;
        transform: translateY(8px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.belimo-ra__step.is-active {
    display: block;
}

.belimo-ra__step h3 {
    margin: 0 0 0.75rem 0;
    font-size: 1rem;
    font-weight: 600;
    color: #1a1a2e;
    letter-spacing: -0.01em;
    font-family: "Roboto", "Helvetica Neue", sans-serif;
}

.belimo-ra__step p {
    margin: 0 0 1rem 0;
    font-size: 0.875rem;
    line-height: 1.65;
    color: #4b5563;
}

.belimo-ra__step ul,
.belimo-ra__step ol {
    margin: 0 0 1rem 0;
    padding-left: 1.25rem;
    font-size: 0.875rem;
    line-height: 1.65;
    color: #4b5563;
}

.belimo-ra__step li {
    margin-bottom: 0.5rem;
}

.belimo-ra__step li:last-child {
    margin-bottom: 0;
}

.belimo-ra__step strong {
    color: #1a1a2e;
    font-weight: 600;
}

.belimo-ra__hint {
    font-size: 0.8125rem;
    color: #6b7280;
    background: rgba(0, 0, 0, 0.02);
    border-left: 3px solid rgba(0, 0, 0, 0.08);
    padding: 0.75rem 1rem;
    border-radius: 0 6px 6px 0;
    margin-top: 1rem;
}

/* ─── CTA Section ─── */
.belimo-ra__cta {
    margin-top: 1.5rem;
    padding: 1.25rem;
    background: linear-gradient(135deg,
        rgba(33, 150, 243, 0.04) 0%,
        rgba(33, 150, 243, 0.08) 100%);
    border-radius: 10px;
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
    align-items: flex-start;
}

.belimo-ra__link {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.625rem 1.25rem;
    background: var(--tb-primary-500, #2196f3);
    color: #ffffff;
    font-size: 0.875rem;
    font-weight: 500;
    text-decoration: none;
    border-radius: 6px;
    transition: all 0.2s ease;
    font-family: "Roboto", "Helvetica Neue", sans-serif;
}

.belimo-ra__link:hover {
    background: var(--tb-primary-600, #1976d2);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(33, 150, 243, 0.3);
}

.belimo-ra__ctaNote {
    font-size: 0.75rem;
    color: #6b7280;
}

/* ─── Navigation ─── */
.belimo-ra__nav {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;
    padding-top: 1.25rem;
    border-top: 1px solid rgba(0, 0, 0, 0.06);
    margin-top: auto;
}

.belimo-ra__btn {
    padding: 0.625rem 1.5rem;
    font-size: 0.875rem;
    font-weight: 500;
    border: 1px solid rgba(0, 0, 0, 0.12);
    border-radius: 6px;
    background: #ffffff;
    color: #4b5563;
    cursor: pointer;
    transition: all 0.2s ease;
    font-family: "Roboto", "Helvetica Neue", sans-serif;
}

.belimo-ra__btn:hover:not(:disabled) {
    border-color: rgba(0, 0, 0, 0.24);
    background: rgba(0, 0, 0, 0.02);
}

.belimo-ra__btn:disabled {
    opacity: 0.4;
    cursor: not-allowed;
}

.belimo-ra__btn--primary {
    border-color: var(--tb-primary-500, #2196f3);
    background: var(--tb-primary-500, #2196f3);
    color: #ffffff;
}

.belimo-ra__btn--primary:hover:not(:disabled) {
    background: var(--tb-primary-600, #1976d2);
    border-color: var(--tb-primary-600, #1976d2);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(33, 150, 243, 0.25);
}

.belimo-ra__navMeta {
    font-size: 0.8125rem;
    color: #9ca3af;
    font-weight: 500;
}

/* ─── Responsive ─── */
@media (max-width: 600px) {
    .belimo-ra {
        padding: 1.25rem;
        gap: 1.25rem;
    }

    .belimo-ra__title {
        font-size: 1.125rem;
    }

    .belimo-ra__dot {
        width: 28px;
        height: 28px;
        font-size: 0.6875rem;
    }

    .belimo-ra__btn {
        padding: 0.5rem 1rem;
        font-size: 0.8125rem;
    }
}

