settings.markdownTextFunction:
/**
 * Asset Report embed (Option C: token exchange)
 *
 * Expected backend (exchangeUrl) behavior:
 * - POST exchangeUrl
 * - Headers: Authorization: Bearer <ThingsBoard JWT>, Content-Type: application/json
 * - Body: { accessCode: "1234567890", entityId?: "...", entityType?: "ASSET", dashboardId?: "...", stateId?: "..." }
 * - Response (JSON): { embedUrl: "https://asset.ecoenergygroup.com/embed/<one-time-token>", expiresAt?: 1730000000000 }
 *
 * Notes:
 * - This function uses a synchronous XHR on purpose to be able to return HTML immediately.
 * - Ensure CORS allows the ThingsBoard origin and the Authorization header on exchangeUrl.
 */
function getTbJwt(ctx) {
  try {
    var $injector = ctx && ctx.$scope && ctx.$scope.$injector;
    if ($injector) {
      var userService = $injector.get('userService');
      if (userService && userService.getJwtToken) {
        var t = userService.getJwtToken();
        if (t) return t;
      }
    }
  } catch (e) { /* ignore */ }

  // Fallbacks (depending on TB version/build)
  try {
    return (
      localStorage.getItem('jwt_token') ||
      localStorage.getItem('jwtToken') ||
      localStorage.getItem('tbJwtToken') ||
      localStorage.getItem('token') ||
      ''
    );
  } catch (e2) {
    return '';
  }
}

function syncPostJson(url, payload, bearerToken) {
  var xhr = new XMLHttpRequest();
  xhr.open('POST', url, false); // synchronous
  xhr.setRequestHeader('Content-Type', 'application/json;charset=UTF-8');
  if (bearerToken) {
    xhr.setRequestHeader('Authorization', 'Bearer ' + bearerToken);
  }
  xhr.send(JSON.stringify(payload || {}));

  var ok = xhr.status >= 200 && xhr.status < 300;
  if (!ok) {
    var msg = (xhr.responseText || '').slice(0, 500);
    throw new Error('Token-exchange failed (' + xhr.status + '): ' + msg);
  }
  try {
    return JSON.parse(xhr.responseText || '{}');
  } catch (e) {
    throw new Error('Token-exchange returned non-JSON response.');
  }
}

var exchangeUrl = (ctx.settings && ctx.settings.exchangeUrl) || 'https://asset.ecoenergygroup.com/api/embed/session';
var accessCode  = (ctx.settings && ctx.settings.accessCode) || '';
var heightPx    = (ctx.settings && ctx.settings.embedHeightPx) || 800;
var sandboxAttr = (ctx.settings && ctx.settings.sandbox) || 'allow-scripts allow-same-origin allow-forms allow-popups';

var entityId = (data && data[0] && (data[0].entityId || (data[0].entity && data[0].entity.id))) || null;
var entityType = (data && data[0] && (data[0].entityType || (data[0].entity && data[0].entity.entityType))) || null;

// Cache to avoid re-exchanging on every widget refresh
window.__assetReportEmbedCache = window.__assetReportEmbedCache || {};
var cacheKey = String(entityType || '') + ':' + String(entityId || '') + ':' + String(accessCode || '');
var cached = window.__assetReportEmbedCache[cacheKey];
if (cached && cached.embedUrl && (!cached.expiresAt || cached.expiresAt > Date.now() + 30000)) {
  return (
    '<div style="width:100%;height:' + heightPx + 'px;">' +
      '<iframe ' +
        'src="' + cached.embedUrl + '" ' +
        'style="width:100%;height:100%;border:0;" ' +
        'loading="lazy" referrerpolicy="no-referrer" ' +
        'sandbox="' + sandboxAttr + '">' +
      '</iframe>' +
    '</div>'
  );
}

var tbJwt = getTbJwt(ctx);
if (!accessCode) {
  return '<div style="padding:12px;">Missing accessCode in widget settings.</div>';
}
if (!tbJwt) {
  return '<div style="padding:12px;">Unable to read ThingsBoard JWT (user is not authenticated or storage access is blocked).</div>';
}

// Build exchange payload (extend as needed)
var payload = {
  accessCode: String(accessCode),
  entityId: entityId,
  entityType: entityType,
  dashboardId: (ctx.dashboard && ctx.dashboard.dashboardId) || null,
  stateId: (ctx.stateController && ctx.stateController.stateId) || null
};

try {
  var resp = syncPostJson(exchangeUrl, payload, tbJwt);
  if (!resp || !resp.embedUrl) {
    return '<div style="padding:12px;">Token-exchange succeeded but no embedUrl returned.</div>';
  }
  window.__assetReportEmbedCache[cacheKey] = {
    embedUrl: resp.embedUrl,
    expiresAt: resp.expiresAt || null
  };
  return (
    '<div style="width:100%;height:' + heightPx + 'px;">' +
      '<iframe ' +
        'src="' + resp.embedUrl + '" ' +
        'style="width:100%;height:100%;border:0;" ' +
        'loading="lazy" referrerpolicy="no-referrer" ' +
        'sandbox="' + sandboxAttr + '">' +
      '</iframe>' +
    '</div>'
  );
} catch (err) {
  return (
    '<div style="padding:12px;">' +
      '<div style="font-weight:600;margin-bottom:6px;">Asset report embed error</div>' +
      '<div style="white-space:pre-wrap;opacity:0.85;">' + ('' + err) + '</div>' +
      '<div style="margin-top:10px;opacity:0.8;">Check CORS on exchangeUrl (Authorization header), and backend response format.</div>' +
    '</div>'
  );
}

