/* ═══════════════════════════════════════════════════════════════
   ECO ENERGY - TABLET HOME TILES (600-959px)
   Industrial Precision Design | 3+2 Grid Layout
   Font: DM Sans | Accent: #1976D2 | Touch-Optimized
   ═══════════════════════════════════════════════════════════════ */

@import url('https://fonts.googleapis.com/css2?family=DM+Sans:opsz,wght@9..40,400;9..40,500;9..40,600;9..40,700&family=JetBrains+Mono:wght@500&display=swap');

:host {
    display: block;
    height: 100%;
    --eco-primary: #1976D2;
    --eco-primary-light: #42A5F5;
    --eco-surface: #FFFFFF;
    --eco-bg: #F5F7FA;
    --eco-text: #1A1F36;
    --eco-text-muted: #5E6278;
    --eco-border: rgba(0, 0, 0, 0.06);
    --eco-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.04);
    --eco-shadow-md: 0 4px 12px rgba(0, 0, 0, 0.08);
    --eco-radius: 8px;
    --eco-transition: 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

/* ─── Container: 3+2 Grid ─── */
.tile-container {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    width: 100%;
    height: 100%;
    justify-content: center;
    align-content: center;
    padding: 1.25rem;
    box-sizing: border-box;
    background: var(--eco-bg);
    overflow-y: auto;
}

/* ─── Card Base ─── */
.eco-card {
    flex: 0 0 calc(33.333% - 0.75rem);
    min-height: 200px;
    max-height: 240px;
    background: var(--eco-surface);
    border-radius: var(--eco-radius);
    box-shadow: var(--eco-shadow-md);
    border: 1px solid var(--eco-border);
    display: flex;
    flex-direction: column;
    position: relative;
    overflow: hidden;
    cursor: pointer;
    transition: transform var(--eco-transition), box-shadow var(--eco-transition);
}

/* Bottom row: 2 cards centered, slightly wider */
.eco-card:nth-child(4),
.eco-card:nth-child(5) {
    flex: 0 0 calc(40% - 0.5rem);
    max-width: 280px;
}

.eco-card:active {
    transform: scale(0.98);
}

/* ─── Accent Line ─── */
.card-accent {
    height: 3px;
    background: linear-gradient(90deg,
        var(--eco-primary) 0%,
        var(--eco-primary-light) 100%);
    flex-shrink: 0;
}

/* ─── Content ─── */
.card-content {
    flex: 1;
    padding: 1rem;
    display: flex;
    flex-direction: column;
    position: relative;
    min-height: 0;
}

/* ─── Number Badge ─── */
.card-number {
    position: absolute;
    top: 0.75rem;
    right: 0.75rem;
    font-family: 'JetBrains Mono', monospace;
    font-size: 0.5625rem;
    font-weight: 500;
    letter-spacing: 0.05em;
    color: var(--eco-primary);
    background: rgba(25, 118, 210, 0.08);
    padding: 0.1875rem 0.375rem;
    border-radius: 3px;
}

/* ─── Icon ─── */
.card-icon-wrapper {
    width: 40px;
    height: 40px;
    border-radius: 8px;
    background: linear-gradient(135deg,
        rgba(25, 118, 210, 0.1) 0%,
        rgba(25, 118, 210, 0.04) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 0.75rem;
    flex-shrink: 0;
}

.card-icon-wrapper mat-icon {
    font-size: 20px;
    width: 20px;
    height: 20px;
    color: var(--eco-primary);
}

/* ─── Typography ─── */
.card-title {
    font-family: 'DM Sans', sans-serif;
    font-size: 0.9375rem;
    font-weight: 600;
    color: var(--eco-text);
    margin: 0 0 0.375rem;
    line-height: 1.3;
    letter-spacing: -0.01em;
}

.card-description {
    font-family: 'DM Sans', sans-serif;
    font-size: 0.75rem;
    line-height: 1.5;
    color: var(--eco-text-muted);
    margin: 0;
    flex: 1;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

/* ─── Footer ─── */
.card-footer {
    padding: 0.625rem 1rem;
    border-top: 1px solid var(--eco-border);
    flex-shrink: 0;
}

.card-link {
    font-family: 'DM Sans', sans-serif;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    font-size: 0.75rem;
    font-weight: 500;
    color: var(--eco-primary);
    text-decoration: none;
}

.card-link .arrow-icon {
    font-size: 14px;
    width: 14px;
    height: 14px;
}

/* ECO ENERGY - MOBILE HOME TILES (≤599px) */

.tile-container {
    display: flex !important;
    flex-direction: column !important;
    gap: 12px !important;
    width: 100% !important;
    height: 100% !important;
    padding: 16px !important;
    box-sizing: border-box !important;
    background: #F5F7FA !important;
    overflow-y: auto !important;
    -webkit-overflow-scrolling: touch !important;
}

.eco-card {
    flex-shrink: 0 !important;
    min-height: 80px !important;
    background: #FFFFFF !important;
    border-radius: 8px !important;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08) !important;
    border: 1px solid rgba(0, 0, 0, 0.06) !important;
    display: flex !important;
    flex-direction: row !important;
    align-items: stretch !important;
    position: relative !important;
    overflow: hidden !important;
    cursor: pointer !important;
}

.eco-card:active {
    transform: scale(0.98) !important;
    background: #FAFBFC !important;
}

.card-accent {
    width: 4px !important;
    min-height: 100% !important;
    background: linear-gradient(180deg, #1976D2 0%, #42A5F5 100%) !important;
    flex-shrink: 0 !important;
}

.card-content {
    flex: 1 !important;
    padding: 12px 16px !important;
    display: grid !important;
    grid-template-columns: 44px 1fr !important;
    grid-template-rows: auto auto !important;
    gap: 4px 14px !important;
    align-items: center !important;
    min-width: 0 !important;
    position: relative !important;
}

.card-icon-wrapper {
    grid-row: 1 / 3 !important;
    grid-column: 1 !important;
    width: 44px !important;
    height: 44px !important;
    border-radius: 10px !important;
    background: linear-gradient(135deg, rgba(25, 118, 210, 0.1) 0%, rgba(25, 118, 210, 0.04) 100%) !important;
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    flex-shrink: 0 !important;
}

.card-icon-wrapper mat-icon {
    font-size: 22px !important;
    width: 22px !important;
    height: 22px !important;
    color: #1976D2 !important;
}

.card-number {
    position: absolute !important;
    top: 8px !important;
    right: 8px !important;
    font-family: 'JetBrains Mono', 'Roboto Mono', monospace !important;
    font-size: 10px !important;
    font-weight: 500 !important;
    letter-spacing: 0.08em !important;
    color: #FFFFFF !important;
    background: #1976D2 !important;
    padding: 3px 6px !important;
    border-radius: 3px !important;
}

.card-title {
