headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
openAddEntityDialog();

function openAddEntityDialog() {
  customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
  let vm = instance;

  vm.allowedEntityTypes = ['ASSET', 'DEVICE'];

  vm.addEntityFormGroup = vm.fb.group({
    entityName: ['', [vm.validators.required]],
    billingType: ['', [vm.validators.required]],
    interval: [null],
    currency: ['EUR', [vm.validators.required]],
    price: [null, [vm.validators.required]],
    prodType: ['analysis', [vm.validators.required]] 
  });

  vm.showInterval = false;

  vm.onBillingTypeChange = function() {
    let billingType = vm.addEntityFormGroup.get('billingType').value;
    vm.showInterval = billingType === 'recurring';
    if (!vm.showInterval) {
      vm.addEntityFormGroup.get('interval').reset();
    }
  };

  vm.cancel = function() {
    vm.dialogRef.close(null);
  };

  vm.save = function() {
    vm.addEntityFormGroup.markAsPristine();
    saveEntityObservable().subscribe(
      function(entity) {
        saveAttributes(entity.id).subscribe(
          function() {
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
          },
          function(error) {
            console.error('Error saving attributes:', error);
          }
        );
      },
      function(error) {
        console.error('Error saving entity:', error);
      }
    );
  };

  function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
          name: formValues.entityName,
          type: "Product"
        };
        return assetService.saveAsset(entity);
  }

  function saveAttributes(entityId) {
    let attributes = vm.addEntityFormGroup.value;
    let attributesArray = [];
    for (let key in attributes) {
      if (attributes[key] !== null && (key !== 'interval' || attributes['billingType'] === 'recurring')) {
        let value = key === 'price' ? attributes[key] * 100 : attributes[key];
        attributesArray.push({ key: key, value: value });
      }
    }
    if (attributesArray.length > 0) {
      return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray).pipe(
        widgetContext.rxjs.catchError((error) => {
          console.error('Error saving attributes:', error);
          return widgetContext.rxjs.throwError(error);
        })
      );
    }
    return widgetContext.rxjs.of([]);
  }
}

