configuration.tbelScript:
// Parse createProject JSON
var projectData = msg.createProject;

// Store all data in metadata for later use
metadata.projectName = projectData.projectName;
metadata.address = projectData.address;
metadata.postalCode = projectData.postalCode;
metadata.city = projectData.city;
metadata.country = projectData.country;
metadata.latitude = projectData.latitude;
metadata.longitude = projectData.longitude;
metadata.normOutdoorTemp = projectData.normOutdoorTemp;
metadata.units = projectData.units;

// Store area as JSON string (Project attribute)
if (projectData.area != null) {
    metadata.area = JSON.stringify(projectData.area);
}

// Store measurements as JSON string
if (projectData.measurements != null) {
    metadata.measurementsJson = JSON.stringify(projectData.measurements);
} else {
    metadata.measurementsJson = '[]';
}

// Store customer info from originator
metadata.customerId = metadata.originatorId;
metadata.customerType = metadata.originatorType;

return { msg: {}, metadata: metadata, msgType: msgType };

// msg now contains the entity groups array from API response
var groups = msg;
var projectsGroupId = null;
var measurementsGroupId = null;

foreach(g : groups) {
    if (g.name == 'Projects') {
        projectsGroupId = g.id.id;
    }
    if (g.name == 'Measurements') {
        measurementsGroupId = g.id.id;
    }
}

metadata.projectsGroupId = projectsGroupId;
metadata.measurementsGroupId = measurementsGroupId;

// Prepare request to get existing projects for numbering
return { msg: {}, metadata: metadata, msgType: msgType };

// msg contains paginated response with data array
var projects = msg.data;
var maxNum = 0;

// Find highest project number
foreach(p : projects) {
    var name = p.name;
    // Extract number from format: SHORTNAME_P-0001
    var parts = name.split('_P-');
    if (parts.length() == 2) {
        var numStr = parts[1];
        var num = parseInt(numStr);
        if (num > maxNum) {
            maxNum = num;
        }
    }
}

var nextNum = maxNum + 1;
var paddedNum = 'P-' + ('0000' + nextNum).substring(nextNum.toString().length());

// Use shortName or fallback to 'PRJ'
var shortName = metadata.ss_shortName;
if (shortName == null || shortName == '') {
    shortName = 'PRJ';
}

var entityName = shortName + '_' + paddedNum;
metadata.projectEntityName = entityName;

// Prepare Project asset body
var projectBody = {
    name: entityName,
    type: 'Project',
    label: metadata.projectName,
    customerId: {
        entityType: 'CUSTOMER',
        id: metadata.customerId
    }
};

return { msg: projectBody, metadata: metadata, msgType: msgType };

// msg contains created project response
metadata.projectId = msg.id.id;
metadata.projectEntityType = 'ASSET';

// Prepare relation body: Customer OWNS Project
var relationBody = {
    from: {
        entityType: 'CUSTOMER',
        id: metadata.customerId
    },
    to: {
        entityType: 'ASSET',
        id: metadata.projectId
    },
    type: 'Owns',
    typeGroup: 'COMMON'
};

return { msg: relationBody, metadata: metadata, msgType: msgType };

// Prepare attributes body for Project
var attributes = {
    progress: 'in preparation',
    state: 'normal',
    address: metadata.address,
    postalCode: metadata.postalCode,
    city: metadata.city,
    country: metadata.country,
    latitude: parseFloat(metadata.latitude),
    longitude: parseFloat(metadata.longitude),
    normOutdoorTemp: parseFloat(metadata.normOutdoorTemp),
    units: metadata.units
};

// Add area if present (JSON object)
if (metadata.area != null) {
    attributes.area = JSON.parse(metadata.area);
}

return { msg: attributes, metadata: metadata, msgType: msgType };

// Parse measurements from metadata and add project info to each
var measurements = JSON.parse(metadata.measurementsJson);
var result = [];
var idx = 0;

foreach(m : measurements) {
    idx = idx + 1;
    // Add project context to each measurement
    m.projectId = metadata.projectId;
    m.projectEntityName = metadata.projectEntityName;
    m.customerId = metadata.customerId;
    m.measurementsGroupId = metadata.measurementsGroupId;
    m.measurementIndex = idx;
    result.add(m);
}

return { msg: result, metadata: metadata, msgType: msgType };

// msg now contains single measurement object
var m = msg;

// Generate measurement entityName: PROJECTNAME_M-01
var paddedIdx = 'M-' + ('00' + m.measurementIndex).substring(m.measurementIndex.toString().length());
var measurementEntityName = m.projectEntityName + '_' + paddedIdx;

// Store for later use
metadata.measurementEntityName = measurementEntityName;
metadata.measurementName = m.measurementName;
metadata.measurementData = JSON.stringify(m);
metadata.projectId = m.projectId;
metadata.customerId = m.customerId;
metadata.measurementsGroupId = m.measurementsGroupId;

// Prepare measurement asset body
var assetBody = {
    name: measurementEntityName,
    type: 'Measurement',
    label: m.measurementName,
    customerId: {
        entityType: 'CUSTOMER',
        id: m.customerId
    }
};

return { msg: assetBody, metadata: metadata, msgType: msgType };

// msg contains created measurement response
metadata.measurementId = msg.id.id;

// Prepare relation body: Project OWNS Measurement
var relationBody = {
    from: {
        entityType: 'ASSET',
        id: metadata.projectId
    },
    to: {
        entityType: 'ASSET',
        id: metadata.measurementId
    },
    type: 'Owns',
    typeGroup: 'COMMON'
};

return { msg: relationBody, metadata: metadata, msgType: msgType };

// Parse measurement data from metadata
var m = JSON.parse(metadata.measurementData);

// Prepare attributes body for Measurement - ALL attributes from Data Catalog
var attributes = {
    progress: 'in preparation',
    state: 'normal',
    installationType: m.installationType,
    systemType: m.systemType,
    measurementType: m.measurementType,
    measurementRole: m.measurementRole,
    hydraulicScheme: m.hydraulicScheme,
    fluidType: m.fluidType,
    designFlowTemp: m.designFlowTemp,
    designReturnTemp: m.designReturnTemp,
    designDeltaT: m.designDeltaT,
    designPower: m.designPower,
    designFlow: m.designFlow,
    pipeDimension: m.pipeDimension,
    valveDimension: m.valveDimension,
    valveKvs: m.valveKvs,
    flowOnThreshold: m.flowOnThreshold,
    hysteresisMinutes: m.hysteresisMinutes,
    weeklySchedule: m.weeklySchedule,
    collapseThreshold: m.collapseThreshold,
    spikeThreshold: m.spikeThreshold,
    cyclingThreshold: m.cyclingThreshold,
    stabilityThreshold: m.stabilityThreshold,
    pumpPresent: m.pumpPresent,
    pumpControlType: m.pumpControlType,
    pumpRatedPower: m.pumpRatedPower,
    auxSensor1: m.auxSensor1,
    auxSensor2: m.auxSensor2,
    calculatePower: m.calculatePower
};

// Remove null/undefined values
var cleanAttributes = {};
var keys = attributes.keys();
foreach(k : keys) {
    if (attributes[k] != null) {
        cleanAttributes[k] = attributes[k];
    }
}

return { msg: cleanAttributes, metadata: metadata, msgType: msgType };
