descriptor.templateCss:
#container tb-markdown-widget {
    height: 100%;
    display: block;
}

#container tb-markdown-widget .tb-markdown-view {
    height: 100%;
    overflow: auto;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.actionWidget.onInit();
}

self.actionSources = function() {
    return {
        'click': {
            name: 'widget-action.click',
            multiple: false
        }
    };
}

self.typeParameters = function() {
    return {
        dataKeysOptional: true,
        datasourcesOptional: true,
        maxDatasources: 1,
        maxDataKeys: 0,
        singleEntity: true,
        previewWidth: '200px',
        previewHeight: '80px',
        embedTitlePanel: true,
        overflowVisible: true,
        hideDataSettings: true
    };
}

self.onDestroy = function() {
}
