descriptor.templateCss:
#container {
    overflow: auto;
}

.tbDatasource-container {
    margin: 5px;
    padding: 8px;
}

.tbDatasource-title {
    font-size: 1.200rem;
    font-weight: 500;
    padding-bottom: 10px;
}

.tbDatasource-table {
    width: 100%;
    box-shadow: 0 0 10px #ccc;
    border-collapse: collapse;
    white-space: nowrap;
    font-size: 1.000rem;
    color: #757575;
}

.tbDatasource-table td {
    position: relative;
    border-top: 1px solid rgba(0, 0, 0, 0.12);
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
    padding: 0px 18px;
    box-sizing: border-box;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.valueCardWidget.onInit();
};

self.onDataUpdated = function() {
    self.ctx.$scope.valueCardWidget.onDataUpdated();
};

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true,
        horizontal: true,
        previewWidth: '420px',
        previewHeight: '90px',
        embedTitlePanel: true,
        supportsUnitConversion: true,
        defaultDataKeysFunction: function() {
            return [{ name: 'vibration', label: 'Vibration', type: 'timeseries' }];
        }
    };
};

self.onDestroy = function() {
};

