descriptor.controllerScript:
var chart = null;
var zoomDebounceTimer = null;

self.onInit = function() {
    self.ctx.$scope.timeSeriesChartWidget.onInit();

    // Wait for chart to be ready, then attach zoom sync handler
    setTimeout(function() {
        attachZoomSyncHandler();
    }, 500);
};

function attachZoomSyncHandler() {
    // Find the ECharts instance in the widget container
    var chartElement = self.ctx.$container.find('div[_echarts_instance_]')[0];
    if (chartElement) {
        var echartsInstance = echarts.getInstanceByDom(chartElement);
        if (echartsInstance) {
            chart = echartsInstance;

            // Listen for datazoom events
            chart.on('datazoom', function(params) {
                handleZoomSync(params);
            });

            console.log('Synced Time Series Chart: Zoom sync handler attached');
        }
    }
}

function handleZoomSync(params) {
    // Debounce to avoid excessive updates during drag
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }

    zoomDebounceTimer = setTimeout(function() {
        // Get the current visible range from the chart
        var option = chart.getOption();
        var dataZoom = option.dataZoom[0];

        // Get the time window bounds
        var timeWindow = self.ctx.timeWindow;
        if (!timeWindow) return;

        var totalRange = timeWindow.maxTime - timeWindow.minTime;

        // Calculate the actual time range from zoom percentages
        var startTime = Math.round(timeWindow.minTime + (totalRange * dataZoom.start / 100));
        var endTime = Math.round(timeWindow.minTime + (totalRange * dataZoom.end / 100));

        // Only update if we're actually zoomed in (not at 0-100%)
        if (dataZoom.start > 0.1 || dataZoom.end < 99.9) {
            // Update the dashboard timewindow - this syncs all widgets using dashboard timewindow
            self.ctx.dashboard.onUpdateTimewindow(startTime, endTime);
        }
    }, 150); // 150ms debounce
}

self.onDataUpdated = function() {
    self.ctx.$scope.timeSeriesChartWidget.onDataUpdated();
}

self.onLatestDataUpdated = function() {
    self.ctx.$scope.timeSeriesChartWidget.onLatestDataUpdated();
}

self.onResize = function() {
    if (chart) {
        chart.resize();
    }
}

self.onDestroy = function() {
    if (chart) {
        chart.off('datazoom');
    }
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }
}

self.typeParameters = function() {
    return {
        previewWidth: '80%',
        embedTitlePanel: true,
        embedActionsPanel: true,
        hasAdditionalLatestDataKeys: true,
        supportsUnitConversion: true,
        dataKeySettingsFunction: TbTimeSeriesChart.dataKeySettings(),
        defaultDataKeysFunction: function() {
            return [{ name: 'temperature', label: 'Temperature', type: 'timeseries', units: 'Â°C', decimals: 0 }];
        }
    };
}

